import React, { Component } from "react";

export default class Affairs extends Component {
  affairs = [
    {
      id: 1,
      data: '12.12.2023',
      time: '14:36',
      text: 'Забрать документы из МФЦ',
      checked: false,
    },
    {
      id: 2,
      data: '12.17.2023',
      time: '17:30',
      text: 'Записаться в зал',
      checked: false,
    },
    {
      id: 3,
      data: '01.04.2024',
      time: '08:30',
      text: 'Поехать во Мценск',
      checked: false,
    },
  ];

  render() {
    if (this.affairs.length > 0) {
      return (
        <div>
          {this.affairs.map((el) => (
            <div className="affair" key={el.id}>
              <h3>{el.data}</h3>
              <h4>{el.time}</h4>
              <p className="paragraph">{el.text}</p>
              <b>{el.checked ? 'Молодец' : 'Ждет выполнения...'}</b>
            </div>
          ))}
        </div>
      );
    } else {
      return (
        <div className="affair">
        <h3>В данный момент никаких задач нет</h3>
      </div>
      )
    }
  }
}
