import React, { Component } from 'react'

export default class AddAffair extends Component {
    constructor(props) {
        super(props);
        this.state = {
          affairs: [
            {
              data: "",
              time: "",
              text: "",
            },
          ],
        };
      }
  render() {
    return (
      <form>
        <input placeholder='Укажите дату' />
        <input placeholder='Укажите время' />
        <textarea placeholder='Текст напоминания'></textarea>
        <button type='button'>Добавить</button>
      </form>
    )
  }
}
